import React, { useEffect } from 'react';
import { useSocket } from '../context/SocketContext';

const ChatPage = () => {
  const {
    userData,
    currentRoom,
    messages,
    usersInRoom,
    sendMessage,
    handleTyping,
  } = useSocket();

  useEffect(() => {
    // scroll to bottom or other side effects
  }, [messages]);

  return (
    <div className="chat-container">
      <h2>Room: {currentRoom}</h2>

      <div className="messages">
        {messages.length === 0 ? (
          <p className="text-gray-400">No messages yet</p>
        ) : (
          messages.map((msg) => (
            <div
              key={msg.id}
              className={`message ${
                msg.isSystem ? 'system-message text-center text-gray-500 italic' : ''
              }`}
              style={{
                backgroundColor: msg.isSystem ? 'transparent' : msg.moodColor || '#ffffff',
                padding: '0.5rem',
                marginBottom: '0.5rem',
                borderRadius: '0.375rem',
              }}
            >
              {msg.isSystem ? (
                <em>{msg.content}</em>
              ) : (
                <>
                  <strong>{msg.username}</strong>: {msg.content}
                </>
              )}
            </div>
          ))
        )}
      </div>

      {/* Example input */}
      <form
        onSubmit={(e) => {
          e.preventDefault();
          const content = e.target.message.value.trim();
          if (content) {
            sendMessage(content, currentRoom);
            e.target.reset();
          }
        }}
      >
        <input
          name="message"
          type="text"
          onChange={handleTyping}
          placeholder="Type a message"
          className="w-full p-2 border rounded"
        />
      </form>
    </div>
  );
};

export default ChatPage;
