import { createContext, useContext, useEffect, useState, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { initSocket, setupSocketListeners } from '../socket/socketClient';

const SocketContext = createContext(null);

export const SocketProvider = ({ children }) => {
  const [socket, setSocket] = useState(null);
  const [userData, setUserData] = useState(null);
  const [currentRoom, setCurrentRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [usersInRoom, setUsersInRoom] = useState([]);
  const [isTyping, setIsTyping] = useState(false);
  const [typingTimeout, setTypingTimeout] = useState(null);
  const navigate = useNavigate();

  // Initialize socket - runs once on mount
  useEffect(() => {
    const newSocket = initSocket();
    setSocket(newSocket);

    return () => {
      newSocket.disconnect();
      if (typingTimeout) clearTimeout(typingTimeout);
    };
  }, []); // typingTimeout is cleared on unmount

  // Memoized system message helper
  const addSystemMessage = useCallback((text) => {
    setMessages(prev => [
      ...prev,
      {
        id: Date.now().toString(),
        content: text,
        userId: 'system',
        username: 'System',
        timestamp: new Date().toISOString(),
        moodColor: '#6b7280',
        isSystem: true
      }
    ]);
  }, []);

  // Consolidated socket event handlers
  const handleSocketEvent = useCallback((event, data) => {
    if (!socket) return;

    switch (event) {
      case 'connect':
        console.log('Connected to server');
        break;
      case 'disconnect':
        console.log('Disconnected from server');
        break;
      case 'connect_error':
        console.error('Connection error:', data);
        break;
      case 'user_data':
        setUserData(data);
        break;
      case 'room_joined':
        setCurrentRoom(data.roomId);
        break;
      case 'user_joined':
        setUsersInRoom(prev => [...prev, data]);
        addSystemMessage(`${data.username} joined the room`);
        break;
      case 'user_left':
        setUsersInRoom(prev => prev.filter(user => user.userId !== data.userId));
        addSystemMessage(`${data.username} left the room`);
        break;
      case 'receive_message':
        setMessages(prev => [...prev, data]);
        break;
      case 'anonymous_toggled':
        setUserData(prev => ({ ...prev, ...data }));
        addSystemMessage(
          data.isAnonymous
            ? 'You are now anonymous'
            : `You are now visible as ${data.username}`
        );
        break;
      case 'language_changed':
        setUserData(prev => ({ ...prev, language: data.language }));
        addSystemMessage(`Language changed to ${data.language}`);
        break;
      case 'room_expired':
        setCurrentRoom(null);
        setMessages([]);
        setUsersInRoom([]);
        addSystemMessage('This room has expired due to inactivity');
        navigate('/chat');
        break;
      default:
        console.warn('Unhandled socket event:', event, data);
    }
  }, [socket, addSystemMessage, navigate]);

  // Setup socket listeners when socket changes
  useEffect(() => {
    if (!socket) return;

    const cleanup = setupSocketListeners(socket, handleSocketEvent);

    return () => {
      cleanup();
      if (typingTimeout) clearTimeout(typingTimeout);
    };
  }, [socket, handleSocketEvent, typingTimeout]);

  // Memoized socket actions
  const socketActions = useMemo(() => ({
    // ** Changed event to 'join_app' per your server code, not 'connect_user' **
    connect: (userId, username, isAnonymous, language) => {
      socket?.emit('join_app', { userId, username, isAnonymous, language });
    },
    joinRoom: (roomId, username) => {
      if (!socket) return;
      socket.emit('join_room', { roomId, username });
      setMessages([]);
      setUsersInRoom([]);
    },
    sendMessage: (content, roomId) => {
      socket?.emit('send_message', { content, roomId });
      setIsTyping(false);
    },
    toggleAnonymous: (isAnonymous) => {
      socket?.emit('toggle_anonymous', { isAnonymous });
    },
    changeLanguage: (language) => {
      socket?.emit('change_language', { language });
    },
    handleTyping: () => {
      if (!socket || !currentRoom) return;

      if (typingTimeout) clearTimeout(typingTimeout);

      setIsTyping(true);
      socket.emit('typing', { roomId: currentRoom, isTyping: true });

      const timeout = setTimeout(() => {
        setIsTyping(false);
        socket.emit('typing', { roomId: currentRoom, isTyping: false });
      }, 2000);

      setTypingTimeout(timeout);
    }
  }), [socket, currentRoom, typingTimeout]);

  // Memoize context value
  const contextValue = useMemo(() => ({
    socket,
    userData,
    currentRoom,
    messages,
    usersInRoom,
    isTyping,
    ...socketActions
  }), [socket, userData, currentRoom, messages, usersInRoom, isTyping, socketActions]);

  return (
    <SocketContext.Provider value={contextValue}>
      {children}
    </SocketContext.Provider>
  );
};

export const useSocket = () => {
  const context = useContext(SocketContext);
  if (!context) {
    throw new Error('useSocket must be used within a SocketProvider');
  }
  return context;
};
