import { useState, useEffect, useRef, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { ThemeContext } from '../context/ThemeContext';
import { useSocket } from '../context/SocketContext'; // Changed from SocketContext to useSocket
import { Button } from '../components/StyledComponents'; // Only import what's available
import { FiSend, FiUser, FiUsers, FiMessageSquare, FiSettings } from 'react-icons/fi';
import { BsEmojiSmile, BsImage, BsCheck2All } from 'react-icons/bs';
import ChatHeader from '../components/hatHeader.js';

// Create local versions of components not available in StyledComponents
const Container = styled.div`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
`;

const Title = styled.h1`
  font-size: 1.5rem;
  color: ${({ theme }) => theme.text};
  margin: 0;
`;

const ToggleButton = styled(Button)`
  padding: 0.5rem;
  background: none;
  border: none;
  font-size: 1.25rem;
`;

const Input = styled.input`
  padding: 0.75rem;
  border-radius: 4px;
  border: 1px solid ${({ theme }) => theme.border};
  background: ${({ theme }) => theme.backgroundSecondary};
  color: ${({ theme }) => theme.text};
  width: 100%;
`;

const ChatPage = () => {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const { theme, toggleTheme } = useContext(ThemeContext);
  const { 
    socket, 
    userData, 
    currentRoom, 
    messages, 
    usersInRoom, 
    isTyping, 
    sendMessage, 
    joinRoom, 
    toggleAnonymous,
    changeLanguage,
  } = useSocket(); // Changed to useSocket
  
  // ... rest of your component logic remains exactly the same ...

  return (
    <ChatContainer>
      <ChatHeader>
        <Title>EchoVerse - {currentRoom || 'New Room'}</Title>
        <HeaderActions>
          <ToggleButton onClick={toggleTheme}>
            {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
          </ToggleButton>
          <SettingsButton onClick={() => setShowSettings(!showSettings)}>
            <FiSettings />
          </SettingsButton>
        </HeaderActions>
      </ChatHeader>

      {/* ... rest of your JSX remains exactly the same ... */}
    </ChatContainer>
  );
};

// Styled Components (only define components that aren't imported)
const ChatContainer = styled(Container)`
  display: flex;
  flex-direction: column;
  height: 100vh;
  padding: 0;
`;

const HeaderActions = styled.div`
  display: flex;
  gap: 0.5rem;
  align-items: center;
`;

const SettingsButton = styled(Button)`
  background: none;
  border: none;
  padding: 0.5rem;
  font-size: 1.25rem;
  color: ${({ theme }) => theme.text};
`;

// ... all other styled component definitions remain the same ...

export default ChatPage;
